    private void ButtonHitungActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonHitungActionPerformed
        if(TextFieldBeratBadan.getText().equals("")||TextFieldTinggi.getText().equals("")||TextFieldNama.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Kotak Isian belum diisi");
        }
        else if(RadioButtonLaki.isSelected()||RadioButtonPerempuan.isSelected()){
            try{
                Proses();
            }
            
            catch(Exception e){
                JOptionPane.showMessageDialog(null, "Inputan ada yang salah");
            }
         
        }
        else{
            //JOptionPane.showMessageDialog(null, "Isikan Jenis Kelamin Anda");
            Object[] options = { "Pesan", "Batal", "Keluar" };
            int optionss = JOptionPane.showOptionDialog(null, "Total biaya keseluruhan adalah : Apakah anda yakin ingin memesan ini?", "Konfirmasi",
            JOptionPane.DEFAULT_OPTION, JOptionPane.INFORMATION_MESSAGE,
                null, options, options[0]);
            
            if (optionss == 0){
                JOptionPane.showMessageDialog(null, "Terimakasih, Pesanan anda akan segera dikirim", "Terimakasih", JOptionPane.INFORMATION_MESSAGE);
            }
            
            else if( optionss == 1)
            {
            
            }
            
            else{
                dispose();
            }
           
        }
    }//GEN-LAST:event_ButtonHitungActionPerformed

