    private void Proses() {
        ti = Double.valueOf(TextFieldTinggi.getText());
        bt = Double.valueOf(TextFieldBeratBadan.getText());
        
        if(RadioButtonPerempuan.isSelected()){
        hl = (ti - 100) * 1;
        }
        else if(RadioButtonLaki.isSelected()){
            hl = (ti - 104) * 1;
        }
        temp = Double.toString(hl.intValue());
        
        if(hl < bt){
            TextFieldIdeal.setText(temp);
            TextFieldDiagnosa1.setText("Maaf "+ TextFieldNama.getText() + ", Sepertinya anda Overweight :(");
            TextFieldDiagnosa2.setText("Bayaklah berolahraga dan hindari makanan berkolestrol");
        }
        else if(hl> bt){
            TextFieldIdeal.setText(temp);
            TextFieldDiagnosa1.setText("Maaf "+ TextFieldNama.getText() + ", Sepertinya anda Underweight :(");
            TextFieldDiagnosa2.setText("Bayaklah mengkonsumsi makanan yang berkarbohidrat");
        }
        else{
            TextFieldIdeal.setText(temp);
            TextFieldDiagnosa1.setText("Hallo "+ TextFieldNama.getText() + ", Berat badan anda sudah ideal :)");
            TextFieldDiagnosa2.setText("Lanjutkan pola makan teratur dan gaya hidup sehat :)");
        }
    }
